import React, { useState } from 'react';
import './index.css';
import { ReactComponent as Compass } from './img/cursor.svg';
import { ReactComponent as Info } from './img/info.svg';
import { ReactComponent as Ai } from './img/artificial-intelligence.svg';
import { ReactComponent as Blog } from './img/blogging.svg';
import { ReactComponent as DataVisualisation } from './img/smartphone.svg';
import { ReactComponent as Rightchevron } from './img/right-chevron.svg';
import { ReactComponent as Leftchevron } from './img/left-chevron.svg';
import { CSSTransition } from 'react-transition-group';

function App() {
  return (
    <div className="App">
      <Navbar>
        <NavItem icon={<Info />} />
        <NavItem icon={<Compass />}> 
          <DropdownMenu />
        </NavItem>
      </Navbar>
    </div>
  );
}

function DropdownMenu() {

  const [activeMenu, setActiveMenu] = useState('main');

  function DropdownItem(props) {
    return (
      <a href="#" className="menu-item">
        <span className='icon-button'>{props.leftIcon}</span>
        {props.children}
        <span className='icon-button' style = {{right:'-60px'}}>{props.rightIcon}</span>
      </a>
    )
  }
  return (
    <div className="dropdown">
      <CSSTransition 
        in={activeMenu === 'main'} 
        unmountOnExit 
        timeout={500}
        classNames= "menu-primary"
      >
        <div className="menu">
          <DropdownItem
            leftIcon = {<Ai />}
            rightIcon={<Rightchevron />}
          >    
            About
          </DropdownItem>
        </div>
      </CSSTransition>
    </div>
  )
}

function Navbar(props) {
   return (
     <nav className='navbar'>
       <ul className='navbar-nav'>
          { props.children}
       </ul>
     </nav>
  )
}
function NavItem(props) {

  const [open, setOpen] = useState(false);

  return (
    <li className='nav-item'>
      <a href='#' className='icon-button' onClick={() => setOpen(!open)}>
        {props.icon}
      </a>

      {open && props.children}
    </li>
  )
}

export default App;
